        int p = 0;
        while (l1 != null && l2 != null) {
            int temp = l1.val + l2.val + p;
            res.add(temp % 10);
            p = temp / 10;
            l1 = l1.next;
            l2 = l2.next;
        }
        while (l1 != null) {
            int temp = l1.val + p;
            res.add(temp % 10);
            p = temp / 10;
            l1 = l1.next;
        }
        while (l2 != null) {
            int temp = l2.val + p;
            res.add(temp % 10);
            p = temp / 10;
            l2 = l2.next;
        }
        if (p != 0) {
            res.add(p);
        }
        ListNode head = new ListNode(res.get(0));
        ListNode ph = head;
        for (int i = 1; i < res.size(); i++) {
            ph.next = new ListNode(res.get(i));
            ph = ph.next;
        }
        return head;
    }
