class Solution {
    public int nthSuperUglyNumber(int n, int[] primes) {
        int[] index = new int[primes.length];
        int[] ugly = new int[n];
        ugly[0] = 1;
        for (int i = 1; i < n; i++) {
            int min = Integer.MAX_VALUE;
            int tempIndex = -1;
            for (int j = 0; j < primes.length; j++) {
                while ((primes[j] * ugly[index[j]]) <= ugly[i-1]) {
                    index[j]++;
